mapper.insert:
INSERT INTO sys_stock_record (
    stock_record_id,
    goods_info_id,
    num,
    type,
    remark,
    create_time,
    create_by
)
VALUES

    (
      #{item.stock_record_id}
      ,#{item.goods_info_id}
      ,#{item.num}
      ,#{item.type}
      ,#{item.remark}
      ,SYSDATE()
      ,#{item.create_by}
    )


    SELECT LAST_INSERT_ID() AS id


INSERT INTO sys_stock (
    goods_info_id,
    total,
    user_id,
    create_time,
    remark,
    deleted
)
VALUES
    (
        #{goods_info_id}
        , #{total}
        , #{user_id}
        , SYSDATE()
        , #{remark}
        ,1
    )

mapper.select:
SELECT sysstock.* , sysgoodsinfo.goods_id , sysgoodsinfo.goods_name , sysgoodsinfo.goods_img_url , sysgoodsinfo.goods_instock_price , sysgoodsinfo.goods_sale_price , sysgoodsinfo.goods_color , sysgoodsinfo.goods_size , sysuser.user_name FROM sys_stock sysstock INNER JOIN sys_goods_info sysgoodsinfo ON sysgoodsinfo.id = sysstock.goods_info_id AND sysgoodsinfo.deleted = 1 LEFT JOIN sys_user sysuser ON sysuser.user_id = sysstock.user_id WHERE sysstock.user_id = #{user_id}
AND sysgoodsinfo.goods_id like CONCAT('%',#{goods_id},'%')
AND sysgoodsinfo.goods_name like CONCAT('%',#{goods_name},'%')
AND sysgoodsinfo.goods_color like CONCAT('%',#{goods_color},'%')
AND sysgoodsinfo.goods_size = #{goods_size}
AND unix_timestamp( sysstock.create_time ) > unix_timestamp(#{start_time})
AND unix_timestamp( sysstock.create_time ) < unix_timestamp(#{end_time})
AND sysstock.total = #{total}
