mapper.select:
SELECT sysstore.store_id ,sysstore.store_name ,sysstore.store_address ,sysstore.store_user_id ,sysstore.store_tel ,sysstore.store_deleted ,sysuser.user_name store_user_name FROM sys_store sysstore LEFT JOIN sys_user sysuser ON sysuser.user_id = sysstore.store_user_id WHERE store_deleted = 1
AND store_id = #{store_id}
AND store_name like CONCAT('%',#{store_name},'%')

SELECT sysuser.user_id ,sysuser.login_account ,sysuser.login_pass ,sysuser.user_name ,sysuser.user_head ,sysuser.user_phone ,sysuser.user_email ,sysuser.user_sex ,sysuser.user_birthday ,sysuser.register_time ,(SELECT user_name from sys_user tmpsysuser where tmpsysuser.user_id = sysuser.user_id) create_by_user_name ,sysuser.create_by ,sysuser.real_name ,sysuser.store_id ,sysstore.store_name ,sysrole.role_value ,sysrole.role_id FROM sys_user sysuser LEFT JOIN sys_store sysstore ON sysuser.store_id = sysstore.store_id AND sysstore.store_deleted = 1 LEFT JOIN sys_user_role sysuserrole ON sysuserrole.user_id = sysuser.user_id LEFT JOIN sys_role sysrole ON sysrole.role_id = sysuserrole.role_id WHERE sysuser.is_shop_keeper NOT IN (1,2)
OR sysuser.user_id = #{store_user_id}
ORDER BY sysuser.register_time DESC

mapper.insert:

	SELECT LAST_INSERT_ID() AS store_id

INSERT INTO sys_store (
	store_name,
	store_address,
	store_user_id,
	store_tel,
	store_deleted
)
VALUES
	(
		#{store_name}
		,#{store_address}
		,#{store_user_id}
		,#{store_tel}
		,1
	)

mapper.update:
UPDATE sys_user
SET
	store_id = #{store_id}
	,is_shop_keeper = #{is_shop_keeper}
WHERE
	user_id = #{user_id}

UPDATE sys_store
SET  store_name = #{store_name}
,store_address =#{store_address}
,store_user_id =#{store_user_id}
,store_tel = #{store_tel}
WHERE store_id =#{store_id}

mapper.select:
SELECT *
FROM sys_store sysstore
WHERE sysstore.store_id = #{store_id}
