mapper.select:
SELECT *
FROM sys_store sysstore
WHERE sysstore.store_user_id = #{store_user_id}

SELECT sysrole.* ,sysuser.user_name FROM sys_role sysrole LEFT JOIN sys_user sysuser ON sysrole.create_by_user_id = sysuser.user_id WHERE sysrole.delete_flag = 0
AND sysrole.role_value like concat(concat('%',#{role_value}),'%')
AND sysrole.create_by_user_id = #{create_by_user_id}
OR sysrole.role_id = #{role_id}

mapper.insert:
INSERT INTO `sys_role_authority` (
`menu_code`,
`role_key`,
`menu_type`
) SELECT

#{menu_code}
,role_key
,#{menu_type}
FROM
sys_role sysrole
WHERE
sysrole.role_id = #{role_id}

mapper.update:
UPDATE sys_user_role
SET role_id= #{role_id}
WHERE user_id =#{user_id}

UPDATE sys_user
SET
    user_name=#{user_name}
    ,user_head=#{user_head}
    ,user_email=#{user_email}
    ,user_sex = #{user_sex}
    ,user_birthday=#{user_birthday}
    ,real_name=#{real_name}

    ,login_pass = #{login_pass}

WHERE user_id = #{user_id}
